import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            textAreaValue: 'TextArea valuedqsgqfsdf',
            imgSrc:
                'https://upload.wikimedia.org/wikipedia/commons/thumb/2/2c/Location_Antarctica.svg/langfr-560px-Location_Antarctica.svg.png',
        };
    }

    handleChange = event => {
        this.setState({ textAreaValue: event.target.value });
    };

    render() {
        return (
            <div className="App">
                <body>
                    <div class="wrapper">
                        <header class="header">
                            Les Manchots d'Antarctique
                        </header>
                        <article class="main">
                            <p>
                                Pellentesque habitant morbi tristique senectus
                                et netus et malesuada fames ac turpis egestas.
                                Vestibulum tortor quam, feugiat vitae, ultricies
                                eget, tempor sit amet, ante. Donec eu libero sit
                                amet quam egestas semper. Aenean ultricies mi
                                vitae est. Mauris placerat eleifend leo.
                            </p>
                        </article>
                        <aside class="WorldMap">
                            <img
                                className="centerImage"
                                src="https://tse1.mm.bing.net/th?id=OIP.YPhOa_qtz5G_aL4hftSeQAHaFb&pid=Api"
                                height="150"
                                width="150"
                                alt="Carte de l'antarctique"
                            />
                        </aside>
                        <aside class="aside aside-2">Aside 2</aside>
                        <footer class="footer">Footer</footer>
                    </div>
                </body>
            </div>
        );
    }
}

export default App;
